from flask import Flask, send_file ,request

app = Flask(__name__)

@app.route('/read-file')
def read_file():
    # 文件路径，请根据实际情况修改
    file_path = "/picture/"+request.args.get('file_path')

    try:
        # 以只读模式打开文件
        with open(file_path, 'r', encoding='utf-8') as file:
            # 读取文件内容
            content = file.read()

        # 返回文件内容
        return content
    except FileNotFoundError:
        return "文件未找到", 404
    except Exception as e:
        return str(e), 500

if __name__ == '__main__':
    app.run(debug=True)
